import logo from './logo.svg';
import add_image from './add_image.png';
import './App.css';
import React, { useEffect, useState, Component } from 'react';
import Subject from './Subject.jsx';
import userService from './userService.js'

class about_edit_user extends Component {
    constructor(props) {
        super(props)

        this.state = {
            userAbout: []
        }
    }

    componentDidMount() {
        userService.getUser().then((res) => {
            this.setState({ userAbout: res.data });
        });
    }

    render() {
        return (
            <div>
                <Subject />
                <main className="" id="main-collapse">
                    <div className="row">
                        {
                            this.state.userAbout.map(
                                board =>
                                <div>
                                {/* <p>{board.profilePic}</p> */}
                                <img className="img-responsive" alt={add_image} src={board.profilePic} />
                                    </div>
                            )
                        }
                        {/* <img className="img-responsive" alt="" src={add_image} /> */}
                        <div className="col-xs-12 col-md-6">
                            {
                                this.state.userAbout.map(
                                    board =>
                                    <textarea className="form-control" rows="5" value={board.about} ></textarea>
                                )
                            }

                            <div className="form-group">
                                <textarea className="form-control" rows="25" placeholder="Enter your message"></textarea>
                            </div>
                            <button type="submit" className="btn btn-primary btn-lg">Save</button>
                        </div>
                    </div>
                </main>
            </div>
        )
    }
}

export default about_edit_user;
