import React, { Component } from 'react'
import userService from './userService.js';

class Update_about_user extends Component {
    constructor(props) {
        super(props)
        this.state = {
            user_email: ''
        }
    }

    componentDidMount() {
        userService.updateEmail(this.state.user_email).then((res) => {
            this.setState({ user_email: res.data });
        });
    }

    updateEmail = (e) => {
        e.preventDefault();
        let updateData = { user_email: this.state.user_email }
        console.log('email => ' + JSON.stringify(updateData));
        userService.updateEmail(updateData).then(res => {
            console.log('email => ' + JSON.stringify(updateData));
            this.props.history.push('/contact');
        });
    }

    changeEmailHandler= (event) => {
        this.setState({user_email: event.target.value});
    }

    render() {
        return (
            <div>
                <div className="form-group" >
                <input type="text" className="form-control" name="user_email" placeholder="Your Email" value={this.state.user_email} onChange={this.changeEmailHandler} />
                </div>
                <button type="submit" className="btn btn-primary btn-lg" onClick={this.updateEmail}>Save</button>
            </div>
        )
    }
}

export default Update_about_user;
