import logo from './logo.svg';
import './App.css';
import React, { useEffect, useState, Component } from 'react';
import Subject from './Subject.jsx';
import userService from './userService.js'

class contact_edit_user extends Component {
    constructor(props) {
        super(props)

        this.state = {
            userAbout: [],
            user_email: ''
        }
    }

    // constructor(props){
    //     super(props);

    //     this.state ={
    //     }
    // }

    // onChange = (e) => {
    //     this.setState({
    //         [e.target.name] : e.target.value
    //     })
    // }

    updateUserEmail = (e) => {
        e.preventDefault();

        // let user = {
        //     user_email: this.state.user_email
        // }

        fetch("http://localhost:8085/update?user_email"+user_email, {
      method: "POST",
      headers: new Headers({
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }),
    })
        .then((response) => response.json())
        .then((result) => {
          console.log("백엔드에서 오는 응답 메세지 ", result);
          if(result.toString() == "true"){
            navigate("../",  { replace: true});
          } else{
            alert('로그인에 실패했습니다. 다시 시도해주세요.');
          }

        });
    }

    componentDidMount() {
        userService.getUser().then((res) => {
            this.setState({ userAbout: res.data });
        });
    }

    render() {
        return (
            <div>
                <Subject />
                <main className="" id="main-collapse">
                    <div className="row">
                        <div className="col-xs-12">
                            <div className="section-container-spacer">
                                <h1>Contact</h1>
                                {
                                    this.state.userAbout.map(
                                        board =>
                                            <p>{board.contact}</p>
                                    )
                                }
                            </div>
                            <div className="section-container-spacer">
                                <form action="" className="reveal-content">
                                    <div className="row">
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <input type="email" className="form-control" id="email" placeholder="Email" readOnly />
                                            </div>
                                            <div className="form-group">
                                                <input type="text" className="form-control" id="subject" placeholder="Subject" readOnly />
                                            </div>
                                            <div className="form-group">
                                                <textarea className="form-control" rows="3" placeholder="Enter your message" readOnly />
                                            </div>
                                        </div>
                                        <div className="col-md-6">
                                            <div className="form-group">
                                                {
                                                    this.state.userAbout.map(
                                                        board =>
                                                        <input type="text" className="form-control" name="user_email" placeholder={board.user_email} />
                                                    )
                                                }
                                            </div>
                                            <button type="submit" className="btn btn-primary btn-lg" onClick={updateUserEmail()}>Save</button>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>


                </main>
            </div>
        )
    }
}

export default contact_edit_user;
